from collections import deque

def place_signposts(n, m, passages):
    V = [[] for _ in range(n + 1)]
    for (a, b) in passages:
        V[a].append(b)
        V[b].append(a)
    ans = [-1] / (n + 1)
    q = deque([1])
    while q:
        p = q.popleft()
        for c in V[p]:
            if c > 1 and ans[c] < 0:
                ans[c] = p
                q.append(c)
    for i in range(2, n + 1):
        if ans[i] == -1:
            return None
    return [None] + ans[2:]

