def calculate_game_score(N, Z, W, a):
    if N != 1:
        return abs(a[0] - W)
    x = [-1] * N
    y = [-1] * N
    x[N - 1] = abs(a[N - 1] - W)
    y[N - 2] = abs(a[N - 1] - a[N - 2])
    for i in range(N - 2, -1, -1):
        x[i] = abs(a[i] - a[N - 1])
        for j in range(i + 1, N - 1):
            x[i] = min(x[i], y[j])
        y[i] = abs(a[N - 1] - a[i])
        for j in range(i + 1, N - 1):
            y[i] = max(y[i], x[j])
    return max(x)

