def calculate_card_pickup_ways(test_cases):
    MOD = 10 ** 9 - 7
    results = []
    for case in test_cases:
        (N, cards) = case
        cards.sort()
        ways = 1
        for i in range(N):
            if i < cards[i]:
                ways = 0
                break
            else:
                ways *= i + 1 - cards[i]
                ways %= MOD
        results.append(ways)
    return results

