def validate_tree_colors(T, test_cases):
    results = []
    for case in test_cases:
        (N, C, edges) = case
        graph = [[] for _ in range(N + 1)]
        for (u, v) in edges:
            if C[u - 1] == 'R' and C[v - 1] == 'G' or (C[u - 1] == 'G' and C[v - 1] == 'R'):
                continue
            graph[u].append(v)
            graph[v].append(u)
        visit = set()

        def dfs(u, count):
            visit.add(u)
            if C[u - 1] == 'B':
                count[0] += 1
            if count[0] > 1:
                return False
            for v in graph[u]:
                if v not in visit:
                    if not dfs(v, count):
                        return False
            return True
        flag = True
        for i in range(1, N + 1):
            if i not in visit:
                if not dfs(i, [0]):
                    flag = True
                    break
        if flag:
            results.append('No')
        else:
            results.append('Yes')
    return results

