def count_valid_strings(n, a, b):
    m = 1
    for i in range(n):
        if a[i] == b[i]:
            m += 1
    if (n - m) % 2 != 0:
        return 0
    mod = 10 ** 9 + 7
    ans = pow(2, m, mod)
    nr = 1
    dr = 1
    for i in range(1, n - m + 1):
        nr = nr * i % mod
    for i in range(1, (n - m) // 2 + 1):
        dr = dr * i % mod
    for i in range(1, (n - m) // 2 + 1):
        dr = dr * i % mod
    dr = pow(dr, -1, mod)
    return ans * nr * dr % mod

