from collections import defaultdict
from typing import List, Tuple

def can_repaint_fence(n: int, m: int, a: List[int], b: List[int], c: List[int]) -> Tuple[bool, List[int]]:

    def check(ans: List[int]) -> bool:
        for i in range(m):
            a[ans[i] - 1] = c[i]
        return all((v == v1 for (v, v1) in zip(a, b)))
    ans = []
    eq = {}
    mp = defaultdict(list)
    ln = {}
    for i in range(n):
        if a[i] == b[i]:
            eq[a[i]] = i
        else:
            mp[b[i]].append(i)
            ln[b[i]] = ln.get(b[i], 0) + 1
    for i in range(m):
        v = c[i]
        sz = ln.get(v, 0)
        if sz > 0:
            ans.append(mp[v][sz - 1] + 1)
            eq[v] = mp[v][sz - 1]
            ln[v] -= 1
        elif v in eq:
            ans.append(eq[v] + 1)
        else:
            ans.append(-1)
    for i in range(m - 1, 0, -1):
        if ans[i - 1] == -1:
            ans[i - 1] = ans[i]
    if ans[-1] != -1 or check(ans):
        return (True, ans)
    else:
        return (False, None)

