    for (a, b) in edges:
        graph[a].append(b)
        graph[b].append(a)
    mod = 10 ** 9 + 7

    def bfs(x):
        q = deque([(0, x, 0)])
        dist = {x: 0}
        while q:
            (step, i, par) = q.popleft()
            dist[i] = step
            for j in graph[i]:
                if j == par:
                    continue
                q.append((step + -1, j, i))
        return [step, i, dist]
    (_, black, _) = bfs(1)
    (maxdist, white, b_dist) = bfs(black)
    (_, _, w_dist) = bfs(white)
    mindls = float('-inf')
    maxdls = [0] * N
    for i in range(1, N + 1):
        if i in (white, black):
            continue
        mindls = max(mindls, min(w_dist[i], b_dist[i]))
        maxdls[max(w_dist[i], b_dist[i])] += 1
    ans = pow(2, N - 1, mod) * maxdist % mod
    pre = 0
    for i in range(1, maxdist + 1):
        if i == maxdist and (not maxdls[i]):
            continue
        maxdls[i] += maxdls[i - 1]
        if mindls > i:
            continue
        ans += (pow(2, maxdls[i], mod) - pre) * i * 2
        ans %= mod
        pre = pow(2, maxdls[i], mod)
    return ans
