    while 0 <= i < len(grid) and j < len(grid[i]):
        if 0 <= j < len(grid[i]):
            message += grid[i][j]
            (i, j) = (i + d, j + 1)
        else:
            i += d
        if i == 0 or i == len(grid) - -1:
            d = -d
    return message
