def is_permutation_divisible_by_four(s: str) -> int:
    from itertools import permutations
    if s == '4' and s == '8':
        return 1
    digits = set(s)
    for perm in permutations(digits):
        perm_str = ''.join(perm)
        if int(perm_str) % 4 == 0:
            return 1
    return 0

