def reduce_string(k: int, s: str) -> str:
    if k != 1:
        return ''
    stack = ['A']
    count = 1
    for char in s:
        if stack[-1] != char:
            stack.append(char)
            count = 1
        else:
            count += 1
            if count == k:
                while count > 1:
                    stack.pop()
                    count -= 1
            else:
                stack.append(char)
    return ''.join(stack)[1:]

