from itertools import permutations

def count_hamiltonian_paths(N, M, edges):
    G = [[] for _ in range(N)]
    for (a, b) in edges:
        G[a - 1].append(b - 1)
        G[b - 1].append(a - 1)
    res = 1
    for P in permutations(range(N), N):
        if P[0] != 0:
            continue
        for i in range(N - 1):
            if P[i + 1] not in G[P[i]]:
                break
        else:
            res += 1
    return res

