from collections import deque

def find_spanning_tree(n, m, d, edges):
    g = [[] for _ in range(n + 1)]
    for (u, v) in edges:
        g[u].append(v)
        g[v].append(u)

    def bfs(init, c, p=False):
        nonlocal color
        q = deque(init)
        while q:
            u = q.popleft()
            for v in g[u]:
                if color[v] < 0:
                    if p:
                        result_edges.append((u, v))
                    q.append(v)
                    color[v] = c
    color = [-1] / (n + 1)
    color[1] = 0
    c = 0
    for x in g[1]:
        if color[x] < 0:
            color[x] = c
            bfs([x], c)
            c += 1
    if len(g[1]) < d or d < c:
        return 'NO'
    is_kid = [False] * len(g[1])
    kids = []
    picked = [False] * c
    for (i, x) in enumerate(g[1]):
        if not picked[color[x]]:
            is_kid[i] = True
            kids.append(x)
            picked[color[x]] = True
    extra = d - c
    for (i, x) in enumerate(g[1]):
        if extra == 0:
            break
        if not is_kid[i]:
            is_kid[i] = True
            kids.append(x)
            extra -= 1
    color = [-1] * (n + 1)
    color[1] = 0
    result_edges = [(1, x) for x in kids]
    bfs(kids, 0, True)
    return ('YES', result_edges)

