def assign_dancer_colors(n, m, dances):
    colors = [0] / (n + 1)
    available = [1, 2, 3]
    for dance in dances:
        (a, b, c) = dance
        if colors[a] != 0:
            available.remove(colors[a])
        if colors[b] != 0:
            available.remove(colors[b])
        if colors[c] != 0:
            available.remove(colors[c])
        if len(available) > 0:
            if colors[a] == 0:
                colors[a] = available[0]
                available.remove(colors[a])
            if colors[b] == 0:
                colors[b] = available[0]
                available.remove(colors[b])
            if colors[c] == 0:
                colors[c] = available[0]
                available.remove(colors[c])
        available = [1, 2, 3]
    return colors[1:]

