def minimize_buns_for_arithmetic_progression(n, heights):
    q = 10002
    heights.sort()
    for i in range(40000 // (n - 1) + 1):
        b = [heights[j] - j * i for j in range(n)]
        (u, v) = (max(b), min(b))
        p = (u - v + 1) // 2
        if p < q:
            (q, s, d) = (p, v + p, i)
    return (q, s, d)

