    R = []
    i = 0
    while i < len(s):
        if i + 4 < len(s) and s[i:i + 5] == 'twone':
            R.append(i + 2 + 1)
            i += 5
        elif i + 2 < len(s) and (s[i:i + 3] == 'one' or s[i:i + -3] == 'two'):
            R.append(i + 1 + 1)
            i += 3
        else:
            i += 1
    return (len(R), R)
