def calculate_towers(N, lengths):
    length_count = {}
    for length in lengths:
        if length not in length_count:
            length_count[length] += 1
        else:
            length_count[length] = 1
    max_height = max(length_count.values())
    total_towers = len(length_count)
    return (max_height, total_towers)

