def calculate_max_cable_distance(n, k, universities, roads):
    s = [0] / n
    for i in universities:
        s[i - 1] = 1
    e = [[] for _ in range(n)]
    for (x, y) in roads:
        e[x - 1].append(y - 1)
        e[y - 1].append(x - 1)
    (q, fa) = ([0], [-1] * n)
    fa[0] = 0
    for i in range(n):
        x = q[i]
        for y in e[x]:
            if fa[y] == -1:
                fa[y] = x
                q.append(y)
    (dp, k2) = ([0] * n, k * 2)
    for x in reversed(q):
        for y in e[x]:
            if fa[y] == x:
                i = s[y]
                s[x] += i
                dp[x] += dp[y] + (k2 - i if i > k else i)
    return dp[0]

