def calculate_min_bottles_and_time(n, a, b):
    d = [[None] / 10001 for _ in range(n)]

    def g(i, s):
        if s <= 0:
            return (0, s)
        if i == n:
            return (10000000.0, 0)
        if not d[i][s]:
            (x, y) = g(i + 1, s - b[i])
            d[i][s] = min(g(i + 1, s), (x + 1, y + b[i] - a[i]))
        return d[i][s]
    (k, t) = g(0, sum(a))
    return (k, t)

