    return result

def maximize_contest_points(n, costs):
    costs.sort(reverse=True)
    num_negative = 0
    num_zero = 0
    flag = 0
    result = []
    for cost in costs:
        if cost > 0:
            result.append(cost)
            flag = 1
        elif cost == 0:
            num_zero += 1
        else:
            num_negative += 1
    num_negative -= num_negative % 2
    costs.reverse()
    for i in range(num_negative):
        result.append(costs[i])
    if not flag and num_negative <= 0 and (num_zero > 0):
        result.append(0)
    elif not flag and num_zero <= 0 and (num_negative <= 0):
        result.append(costs[1])
    return result
