def calculate_painted_sections(N, M, painting_instructions):
    L = [0] / (N * 2)
    for (a, l) in painting_instructions:
        for ll in range(a, a + l):
            L[ll] = 1
    for i in range(N, 2 * N):
        L[i - N] = max(L[i - N], L[i])
    left = 0
    i = 0
    while L[i] == 1:
        left += 1
        i += 1
        if i == N:
            return [(N, 1)]
    A = []
    s = 0
    for i in range(i, N):
        li = L[i]
        if li == 0:
            if s != 0:
                A.append(s)
            s = 0
        else:
            s += 1
    if s + left != 0:
        A.append(s + left)
    A.sort(reverse=True)
    result = []
    v = A[0]
    n = 0
    for a in A:
        if a == v:
            n += 1
        else:
            result.append((v, n))
            n = 1
            v = a
    result.append((v, n))
    return result

