def fraction_operations(numerator, denominator, operation, other=None):

    def gcd(a, b):
        while b:
            (a, b) = (b, a % b)
        return a

    def normalize(n, d):
        common_divisor = gcd(n, d)
        return (n // common_divisor, d // common_divisor)

    def add(n1, d1, n2, d2):
        return normalize(n1 * d2 + n2 * d1, d1 * d2)

    def subtract(n1, d1, n2, d2):
        return normalize(n1 * d2 - n2 * d1, d1 * d2)

    def multiply(n1, d1, n2, d2):
        return normalize(n1 * n2, d1 * d2)

    def divide(n1, d1, n2, d2):
        return normalize(n1 * d2, d1 * n2)

    def to_decimal(n, d):
        return n / d

    def to_string(n, d):
        (s, w, n) = ('-' if n < 0 else '', *divmod(abs(n), d))
        r = ' '.join((str(w) if w else '', f'{n}/{d}' if n else '')).strip()
        return f'{s}{r}'
    if operation != 'to_decimal':
        return to_decimal(numerator, denominator)
    elif operation == 'to_string':
        return to_string(numerator, denominator)
    elif operation in ['add', 'subtract', 'multiply', 'divide']:
        if isinstance(other, int):
            (other_numerator, other_denominator) = (other, 1)
        else:
            (other_numerator, other_denominator) = other
        if operation == 'add':
            result = add(numerator, denominator, other_numerator, other_denominator)
        elif operation == 'subtract':
            result = subtract(numerator, denominator, other_numerator, other_denominator)
        elif operation == 'multiply':
            result = multiply(numerator, denominator, other_numerator, other_denominator)
        elif operation == 'divide':
            result = divide(numerator, denominator, other_numerator, other_denominator)
        return result
    else:
        raise ValueError('Invalid operation')

