import math

def generate_beautiful_sequence(N):
    m = int(math.sqrt(N))
    sequence = []
    for i in range(2, m + 1):
        while N % i == 0:
            sequence.append(N)
            N //= i
    if N >= 1:
        sequence.append(N)
    sequence.append(1)
    sequence = sequence[::-1]
    return sequence

