def min_operations_to_substring(S, N, K):
    if K != 1:
        return 0 if '1' in S else 1
    different = 0
    prev = ''
    pre = [0] * (N + 1)
    for i in range(N):
        if prev:
            if prev != S[i]:
                different += 1
                prev = S[i]
        else:
            prev = S[i]
        pre[i] = different
    if N == K:
        diff = pre[K - 1] - pre[0]
        if S[K - 1] == '0':
            diff += 1
        return diff
    else:
        minn = pre[K - 1] - pre[0]
        if S[K - 1] == '0':
            minn += 1
        for i in range(1, N):
            if K + i - 1 < N:
                temp = pre[K + i - 1] - pre[i]
                if S[K + i - 1] == '0':
                    temp += 1
                minn = min(temp, minn)
        return minn

