def scramble_message(k: int, message: str) -> str:

    def get_binary(num: int, bits: int) -> str:
        ans = ['0'] * bits
        i = bits - 1
        while i >= 0:
            ans[i] = str(num % 2)
            num //= 2
            i -= 1
        return ''.join(ans)
    n = 2 * k
    scrambled_message = ['0'] * n
    for i in range(n):
        bi = get_binary(i, k)
        bi_reversed = bi[::-1]
        x = int(bi_reversed, 2)
        scrambled_message[x] = message[i]
    return ''.join(scrambled_message)

