def count_good_observatories(N, M, H, roads):
    l = [1] / N
    for (a, b) in roads:
        a -= 1
        b -= 1
        if H[a] >= H[b]:
            l[b] = 0
        if H[a] <= H[b]:
            l[a] = 0
    return sum(l)

