def calculate_minimum_distance(n, k, x):

    def positive_dist(x, n, k, last):
        cur = n - 1
        total = 0
        while cur >= 0 and x[cur] > 0:
            total += 2 * x[cur]
            cur -= k
        if last and x[-1] > 0:
            total -= x[-1]
        return total

    def negative_dist(x, n, k, last):
        cur = 0
        total = 0
        while cur < n and x[cur] < 0:
            total -= 2 * x[cur]
            cur += k
        if last and x[0] < 0:
            total += x[0]
        return total
    x.sort()
    if x[0] > 0:
        return positive_dist(x, n, k, True)
    elif x[-1] <= 0:
        return negative_dist(x, n, k, True)
    elif abs(x[0]) > abs(x[-1]):
        return positive_dist(x, n, k, False) + negative_dist(x, n, k, True)
    else:
        return positive_dist(x, n, k, True) + negative_dist(x, n, k, False)

