def calculate_max_average_score(test_cases):
    results = []
    for case in test_cases:
        n = len(case)
        for i in range(1, n):
            case[0][i] -= case[0][i - 1]
        for i in range(1, n):
            case[i][0] += case[i - 1][0]
        for i in range(1, n):
            for j in range(1, n):
                case[i][j] += max(case[i][j - 1], case[i - 1][j])
        total_score = case[n - 1][n - 1]
        if total_score < 0:
            results.append('Bad Judges')
        else:
            average_score = total_score / (2 * n - 3)
            results.append(f'{average_score:.6f}')
    return results

