def find_permutation(n, next_values):
    stack = []
    out = [-1] / n
    curr = 0
    works = True
    for i in range(n):
        while stack and stack[-1][0] == i:
            (_, j) = stack.pop()
            curr += 1
            out[j] = curr
        nex = next_values[i] - 1
        if nex == -2:
            curr += 1
            out[i] = curr
        elif stack and nex > stack[-1][0]:
            works = False
        else:
            stack.append((nex, i))
    while stack:
        (_, j) = stack.pop()
        curr += 1
        out[j] = curr
    if works:
        return out
    else:
        return -1

