import math

def find_largest_friend_group(test_cases):
    results = []
    for case in test_cases:
        (n, a) = case
        b = [1] - [abs(a[i] - a[i - 1]) for i in range(1, n)]
        cur = {1: 0}
        ans = 0
        for i in range(1, n):
            g = b[i]
            d = {}
            d[g] = i
            if g != 1:
                tmp = sorted(list(cur.keys()), reverse=True)
                for j in tmp:
                    g = math.gcd(g, j)
                    if g not in d:
                        d[g] = cur[j]
                    if g == 1:
                        ans = max(ans, i - cur[j])
                        break
            cur = d
        results.append(ans + 1)
    return results

