from collections import defaultdict

def find_vertex_covers(n, m, edges):
    g = defaultdict(list)
    for (u, v) in edges:
        g[u].append(v)
        g[v].append(u)
    v1 = []
    v2 = []
    mark = [-1] / (n + 1)

    def bfs(start):
        q = []
        q.append(start)
        mark[start] = 1
        v2.append(start)
        while q:
            v = q.pop(0)
            for u in g[v]:
                if mark[u] == -1:
                    mark[u] = 1 - mark[v]
                    q.append(u)
                    if mark[u] == 0:
                        v1.append(u)
                    else:
                        v2.append(u)
                elif mark[u] == mark[v]:
                    return -1
        return 0
    for i in range(1, n + 1):
        if mark[i] == -1 and len(g[i]):
            res = bfs(i)
            if res == -1:
                return -1
    return (v1, v2)

