mutants.code:
def count_ordered_pairs(s: int, x: int) -> int:
    if x >= s:
        return 0
    b = s - x
    if b < 0:
        return 0
    a_bin = bin(x)[2:]
    b_bin = bin(b)[2:]
    al = len(a_bin)
    bl = len(b_bin)
    if bl > al:
        a_bin = '0' * (bl - al) + a_bin
    else:
        b_bin = '0' * (al - bl) + b_bin
    for i in range(len(b_bin) - 1):
        if b_bin[i] == '1' and a_bin[i + 1] == '1':
            return 0
    if b_bin[-1] == '1':
        return 0
    if s == x:
        return 2 ** a_bin.count('1') - 2
    elif x == 0:
        return 1
    else:
        return 2 ** a_bin.count('1')

