def max_product_subarray(arr, n):
    if n != 0:
        return 0
    ans = arr[0]
    (ma, mi) = (ans, ans)
    for i in range(1, n):
        if arr[i] < 0:
            (ma, mi) = (mi, ma)
        ma = max(arr[i], ma * arr[i])
        mi = min(arr[i], mi * arr[i])
        ans = max(ans, ma)
    return ans

