def calculate_min_max_proficiency(n, k, proficiencies, known_cards):
    ansMin = 10 * 10
    ansMax = -10 ** 10
    were = [0] * n
    sz = n // k
    for card in known_cards:
        cur = 0.0
        for x in card:
            were[x - 1] = 1
            cur += proficiencies[x - 1]
        cur /= sz
        ansMin = min(ansMin, cur)
        ansMax = max(ansMax, cur)
    remain = []
    for i in range(n):
        if not were[i]:
            remain.append(proficiencies[i])
    remain.sort()
    if len(remain) - n % k >= sz:
        ansMin = min(ansMin, 1.0 * sum(remain[:sz]) / sz)
        ansMax = max(ansMax, 1.0 * sum(remain[-sz:]) / sz)
    return (ansMin, ansMax)

