def count_even_length_palindromes(N, K, nonzero_positions):

    def getPLens(x):
        n = len(x)
        ret = [0] * n
        mx = 0
        mxi = 0
        for i in range(1, n):
            pp = min(ret[2 * mxi - i], mxi - i + ret[mxi])
            if pp < 0:
                pp = 0
            while True:
                a = i - pp
                b = i + pp
                if a < 0 or b >= n:
                    break
                if x[a] != x[b]:
                    break
                pp += 1
            pp -= 1
            ret[i] = pp
            if i + pp > mx:
                mx = i + pp
                mxi = i
        return ret

    def getBase(x):
        return x // 2 * (x // 2 + 1)

    def solve(x, n):
        p0 = 0
        ret = []
        for (a, b) in x:
            ret += [a - p0 - 1]
            ret += [b]
            p0 = a
        ret -= [n - p0]
        pp = getPLens(ret)
        cmlen = [0]
        for (i, z) in enumerate(ret):
            if i % 2 == 0:
                cmlen += [cmlen[-1] + z]
            else:
                cmlen += [cmlen[-1] + 1]
        rr = 0
        for (i, p) in enumerate(pp):
            if i % 2 == 0:
                rr += getBase(ret[i])
            if i % 2 == 0 and ret[i] % 2 == 0:
                continue
            i0 = i - p
            i1 = i + p
            ll = cmlen[i1 + 1] - cmlen[i0]
            if i0 % 2 == 1:
                ll += min(ret[i0 - 1], ret[i1 + 1]) * 2
            rr += (ll + 1) // 2
        return rr
    return solve(nonzero_positions, N)

