def count_rectangles_for_letter(radius: int) -> int:
    """"""
    import math
    max_side = int(math.floor(radius * math.sqrt(2)))
    count = 0
    for length in range(1, max_side + 1):
        for breadth in range(1, max_side + 1):
            if length * breadth <= radius ** 2:
                count += 1
    return count
