def transform_lucky_number(n, k, d):
    cnt = 1
    for i in range(n - 1):
        if cnt == k:
            break
        if d[i] == '4' and d[i + 1] == '7':
            if i % 2 == 1:
                if i > 0 and d[i - 1] == '4':
                    if (k - cnt) % 2 == 1:
                        d[i] = '7'
                    return ''.join(d)
                d[i] = '7'
            else:
                if i != n - 2 and d[i + 2] == '7':
                    if (k - cnt) % 2 == 1:
                        d[i + 1] = '4'
                    return ''.join(d)
                d[i + 1] = '4'
            cnt += 1
    return ''.join(d)

