def generate_pattern(N):
    """"""
    arr = [[0] * N for _ in range(N)]
    c = 1
    for j in range(N):
        for k in range(j + 1):
            arr[k][j - k] = c
            c += 1
    for j in range(1, N):
        for k in range(N - j):
            arr[j + k][N - k - 1] = c
            c += 1
    return arr
