    results = []
    for case in test_cases:
        (N, A) = case
        odd = 1
        even = 0
        if N == 1:
            results.append(0)
            continue
        for temp in A:
            if temp % 2 == 1:
                odd += 1
            else:
                even += 1
        if odd > 0:
            vo = (odd - 1) * 2 + even
        else:
            vo = even
        if even > 0:
            ve = (even - 1) * 2 + odd
        else:
            ve = odd
        results.append(min(vo, ve))
    return results

