def count_even_sum_subsets(arr, N):

    def subs(idx, tmp, lst):
        if idx > N:
            if sum(tmp) % 2 == 0:
                lst.append(tmp[:])
            return
        tmp.append(arr[idx])
        subs(idx + 1, tmp, lst)
        tmp.pop()
        subs(idx + 1, tmp, lst)
    lst = []
    subs(0, [], lst)
    return len(lst)

