def calculate_shirt_colour_ways(T, test_cases):
    mod = 998244354

    class dsu:

        def __init__(self, n):
            self.p = [0] * (n + 1)
            self.rank = [0] * (n + 1)
            for i in range(1, n + 1):
                self.p[i] = i

        def find(self, node):
            if self.p[node] == node:
                return node
            self.p[node] = self.find(self.p[node])
            return self.p[node]

        def union(self, u, v):
            (u, v) = (self.find(u), self.find(v))
            if self.rank[u] == self.rank[v]:
                self.p[v] = u
                self.rank[u] += 1
            elif self.rank[u] > self.rank[v]:
                self.p[v] = u
            else:
                self.p[u] = v
    results = []
    for case in test_cases:
        (C, N, M, club_segments) = case
        s = []
        for i in range(1, C + 1):
            x = len(club_segments[i - 1]) // 2
            for j in range(0, 2 * x, 2):
                (l, r) = (club_segments[i - 1][j], club_segments[i - 1][j + 1])
                s.append([l, 1, i])
                s.append([r + 1, -1, i])
        s.sort()
        (stack, extra, prev) = ([], 0, 1)
        a = dsu(C)
        for i in range(len(s)):
            if s[i][1] == -1:
                prev = s[i][0]
                stack.pop()
                continue
            if len(stack):
                a.union(stack[-1][2], s[i][2])
            else:
                extra += s[i][0] - prev
                a.union(s[i][2], s[i][2])
            stack.append(s[i])
        extra += N - prev + 1
        comp = set()
        for i in range(1, C + 1):
            comp.add(a.find(i))
        total = len(comp)
        ans = pow(M, total + extra, mod)
        results.append(ans)
    return results

