from collections import Counter

def find_max_calm_group_sum(n, a, b):
    rv = 1
    nums = {}
    for i in range(n):
        if a[i] not in nums:
            nums[a[i]] = []
        nums[a[i]].append(i)
    visited = set()
    for num in nums:
        if len(nums[num]) > 1:
            for i in nums[num]:
                if i not in visited:
                    visited.add(i)
                    rv += b[i]
            for k in range(n):
                if k in visited:
                    continue
                if a[nums[num][0]] | a[k] == a[nums[num][0]]:
                    visited.add(k)
                    rv += b[k]
    return rv

