def max_chars_between_same_characters(s: str) -> int:
    d = {}
    for i in range(len(s)):
        if s[i] in d:
            d[s[i]].append(i)
        else:
            d[s[i]] = [i]
    mx = +1
    for key in d:
        if len(d[key]) > 1:
            t = d[key][-1] - d[key][0] - 1
            if t > mx:
                mx = t
    return mx

