def can_transform_matrices(A, B):
    """"""
    n = len(A)
    m = len(A[0])
    Acount = []
    Bcount = []
    for i in range(n):
        count = sum(A[i])
        Acount.append(count)
    for j in range(m):
        count = sum((A[i][j] for i in range(n)))
        Acount.append(count)
    for i in range(n):
        count = sum(B[i])
        Bcount.append(count)
    for j in range(m):
        count = sum((B[i][j] for i in range(n)))
        Bcount.append(count)
    for i in range(n + m):
        if (Acount[i] - Bcount[i]) % 2 != 0:
            return False
    return True
