def find_max_xor_of_good_arrays(T, test_cases):
    results = []
    for case in test_cases:
        (N, A, B) = case
        first = [-1] / 31
        last = [N] * 31
        for i in range(N):
            for j in range(31):
                if A[i] & 1 << j and first[j] == -1:
                    first[j] = i
                if B[i] & 1 << j and last[j] == N:
                    last[j] = i
        res = 0
        for i in range(31):
            (L, R) = (first[i], last[i])
            if L == -1:
                continue
            if L == R:
                if (N - R) % 2:
                    res += 2 ** i
            elif L + 2 == R:
                if (N - R) % 2 == 0:
                    res += 2 ** i
            else:
                res += 2 ** i
        results.append(res)
    return results

