def calculate_awesomeness_sum(n, m, arr, queries):

    def awesomeness_of_subsegments(array):
        dp = [1] * n
        for i in range(1, n):
            dp[i] = dp[i - 1] + 1 if array[i - 1] == array[i] else i + 1
        return sum(dp)
    results = []
    current_awesomeness_sum = awesomeness_of_subsegments(arr)
    for (i, x) in queries:
        i += 1
        if arr[i] != x:
            if i > 0:
                if arr[i - 1] == arr[i]:
                    current_awesomeness_sum += i * (n - i)
                elif x == arr[i - 1]:
                    current_awesomeness_sum -= i * (n - i)
            if i < n - 1:
                if arr[i + 1] == arr[i]:
                    current_awesomeness_sum += (i + 1) * (n - i - 1)
                elif x == arr[i + 1]:
                    current_awesomeness_sum -= (i + 1) * (n - i - 1)
        arr[i] = x
        results.append(current_awesomeness_sum)
    return results

