def smallest_string_after_operations(s: str, K: int) -> str:

    def dist(c: str) -> int:
        return (ord('z') - ord(c) + 1) % 26
    s = list(s)
    i = 1
    while 0 < K and i < len(s):
        d = dist(s[i])
        if d <= K:
            K -= d
            s[i] = 'a'
        i += 1
    s[-1] = chr(ord(s[-1]) + K % 26)
    return ''.join(s)

