def min_operations_to_sort_binary_string(n, s):
    l = 1
    r = n - 1
    left = []
    right = []
    while l < r:
        while l < n and s[l] != '1':
            l += 1
        while r >= 0 and s[r] != '0':
            r -= 1
        if l >= r:
            break
        left.append(l + 1)
        right.append(r + 1)
        l += 1
        r -= 1
    l = len(left)
    r = len(right)
    right = right[::-1]
    if l + r == 0:
        return (0, [])
    else:
        operations = [(l + r, left + right)]
        return (1, operations)

