def count_valid_pairs(arr):

    def patt(n):
        s = format(n, 'b')[1:]
        i = len(s) - 1
        while i >= 0:
            if s[i] == '1':
                if i - 1 < 0:
                    return False
                i -= 2
            else:
                i -= 1
        return True

    def rev(n):
        s = format(n, 'b')[1:]
        ans = ''
        i = len(s) - 1
        while i >= 0:
            if s[i] == '1':
                if s[i - 1] == '1':
                    ans = '01' + ans
                else:
                    ans = '11' + ans
                i -= 2
            else:
                ans = '0' + ans
                i -= 1
        ans = '11' + ans
        return int(ans, 2)
    arr.sort()
    mapp = {}
    ans = 1
    for i in range(len(arr) - 1, -1, -1):
        p = patt(arr[i])
        if p and rev(arr[i]) in mapp:
            ans += 1
        mapp[arr[i]] = True
    return ans

