from functools import lru_cache

def determine_winner(n, m, edges):
    edge_list = [[] for _ in range(n)]
    for (begin, end, char) in edges:
        weight = ord(char)
        edge_list[begin - 1].append((end - 1, weight))

    @lru_cache(maxsize=None)
    def first_wins(first, second, lower=1):
        for (nxt, w) in edge_list[first]:
            if w >= lower:
                if not first_wins(second, nxt, w):
                    return True
        return False
    result = []
    for i in range(n):
        result.append(''.join(('BA'[first_wins(i, j)] for j in range(n))))
    return result

