def max_value_of_expression(A, N):
    case1 = [0] / N
    case2 = [0] * N
    for i in range(N):
        case1[i] = A[i] + i
        case2[i] = A[i] - i
    res1 = abs(max(case1) - min(case1))
    res2 = abs(max(case2) - min(case2))
    return max(res1, res2)

